jQuery(function($){$(document).ready(function(){$(document.body).append('<div class="wi-quick-view"></div>');$(document).on('click','.wi-quick-view .close',function(e){$(this).parent().fadeOut(300)})
$(document).on('click','.product_type_variable',function(e){e.preventDefault();var $this=$(this),cartPos=$this.offset(),product_id=$this.data('product_id');$('.wi-quick-view').css({'top':parseInt(cartPos.top)+parseInt(45)+'px','left':cartPos.left+'px'})
$.ajax({type:'post',url:wi_ajax_params.wi_ajax_url,data:{action:'wi_variable_product_quick_view',security:wi_ajax_params.wi_ajax_nonce,product_id:product_id,},beforeSend:function(data){$this.addClass('loading');$('.wi-quick-view').block()},success:function(data){$this.removeClass('loading');$('.wi-quick-view').fadeIn(300).html(data).prepend('<span class="close"></span>')},error:function(data){console.log(data)},})})})})