 namespace MailPoetVendor\Doctrine\ORM\Internal\Hydration; if (!defined('ABSPATH')) exit; use Iterator; class IterableResult implements Iterator { private $_hydrator; private $_rewinded = \false; private $_key = -1; private $_current = null; public function __construct($hydrator) { $this->_hydrator = $hydrator; } public function rewind() { if ($this->_rewinded === \true) { throw new HydrationException('Can only iterate a Result once.'); } else { $this->_current = $this->next(); $this->_rewinded = \true; } } public function next() { $this->_current = $this->_hydrator->hydrateRow(); $this->_key++; return $this->_current; } public function current() { return $this->_current; } public function key() { return $this->_key; } public function valid() { return $this->_current !== \false; } } 