<?php
 namespace MailPoetVendor\Doctrine\ORM\Mapping\Reflection; if (!defined('ABSPATH')) exit; use MailPoetVendor\Doctrine\Persistence\Mapping\ReflectionService; use ReflectionClass; use ReflectionProperty; use function array_combine; use function array_filter; use function array_map; use function array_merge; use function call_user_func_array; final class ReflectionPropertiesGetter { private $properties = []; private $reflectionService; public function __construct(ReflectionService $reflectionService) { $this->reflectionService = $reflectionService; } public function getProperties($className) : array { if (isset($this->properties[$className])) { return $this->properties[$className]; } return $this->properties[$className] = call_user_func_array( 'array_merge', array_merge([[]], array_map([$this, 'getClassProperties'], $this->getHierarchyClasses($className))) ); } private function getHierarchyClasses(string $className) : array { $classes = []; $parentClassName = $className; while ($parentClassName && ($currentClass = $this->reflectionService->getClass($parentClassName))) { $classes[] = $currentClass; $parentClassName = null; $parentClass = $currentClass->getParentClass(); if ($parentClass) { $parentClassName = $parentClass->getName(); } } return $classes; } private function getClassProperties(ReflectionClass $reflectionClass) : array { $properties = $reflectionClass->getProperties(); return array_filter(array_filter(array_map([$this, 'getAccessibleProperty'], array_combine(array_map([$this, 'getLogicalName'], $properties), $properties))), [$this, 'isInstanceProperty']); } private function isInstanceProperty(ReflectionProperty $reflectionProperty) : bool { return !$reflectionProperty->isStatic(); } private function getAccessibleProperty(ReflectionProperty $property) : ?ReflectionProperty { return $this->reflectionService->getAccessibleProperty($property->getDeclaringClass()->getName(), $property->getName()); } private function getLogicalName(ReflectionProperty $property) : string { $propertyName = $property->getName(); if ($property->isPublic()) { return $propertyName; } if ($property->isProtected()) { return "\0*\0" . $propertyName; } return "\0" . $property->getDeclaringClass()->getName() . "\0" . $propertyName; } } 