			),
		),
		'Posts'      =>
		array(
			0 => array(
				'text'  => __( 'Posts', 'seedprod-pro' ),
				'value' => 'is_single(x)',
			),
			1 => array(
				'text'  => __( 'Has Category', 'seedprod-pro' ),
