function seedprod_pro_login_helper_func( $atts ) {
	$a = shortcode_atts(
		array(
			'block_id'      => '',
			'logged_in_str' => 'Logged in as {user}',
			'redirect_url'  => '',
		),
		$atts
	);

	$selectors = array(
		'form_class'          => 'sp-form-' . $a['block_id'],
		'logged_in_id'        => 'sp-loggedin-' . $a['block_id'],
		'loginform_id'        => 'sp-login-' . $a['block_id'],
		'loginform_submit_id' => 'sp-submit-loginform-' . $a['block_id'],
		'lostpass_link_id'    => 'sp-lostpass-link-' . $a['block_id'],
		'redirect_to_id'      => 'sp-login-redirect-' . $a['block_id'],
	);

	$permalink = get_permalink();

	$redirect_url = esc_url( $a['redirect_url'] );
	$redirect     = empty( $redirect_url ) ? admin_url() : $redirect_url;

	$login_url         = wp_login_url();
	$loginform_script  = "sp.addAttribute('$selectors[loginform_id]', 'action', '$login_url');";
	$loginform_script .= "sp.addAttribute('$selectors[redirect_to_id]', 'value', '$redirect');";

	$login_nonce_html   = wp_nonce_field( 'seedprod-login', 'seedprod-login-nonce' );
	$login_nonce_script = "sp.addNonceField('$selectors[loginform_submit_id]', '$login_nonce_html');";

	$login_lostpass_url    = esc_url( wp_lostpassword_url( $redirect ) );
	$login_lostpass_script = "sp.addAttribute('$selectors[lostpass_link_id]', 'href', '$login_lostpass_url');";

	$init_script = '<script>' .
						$loginform_script .
						$login_nonce_script .
						$login_lostpass_script .
					'</script>';

	if ( is_user_logged_in() ) {
		$current_user     = wp_get_current_user();
		$logout_link      = '<a href="' . wp_logout_url( $permalink ) . '">' . __( 'Logout', 'seedprod' ) . '</a>';
		$logged_in_html   = '<p>' .
			str_replace( '{user}', $current_user->display_name, $a['logged_in_str'] ) .
			'<br>' . $logout_link . '</p>';
