/**
 * Redirect users to Cart / Checkout page after adding item to cart
 */
function seedprod_pro_wc_add_to_cart_redirect( $url ) {
	$add_to_cart             = ! empty( $_REQUEST['add-to-cart'] ) ? sanitize_text_field( wp_unslash( $_REQUEST['add-to-cart'] ) ) : ''; // phpcs:ignore WordPress.Security.NonceVerification.Recommended
	$sp_add_to_cart_redirect = ! empty( $_REQUEST['sp-add-to-cart-redirect'] ) ? sanitize_text_field( wp_unslash( $_REQUEST['sp-add-to-cart-redirect'] ) ) : ''; // phpcs:ignore WordPress.Security.NonceVerification.Recommended

	if ( null === $add_to_cart ||
		! is_numeric( $add_to_cart ) ||
		null === $sp_add_to_cart_redirect ) {
		return $url;
	}

	if ( 'cart' === $sp_add_to_cart_redirect &&
		function_exists( 'wc_get_cart_url' ) ) {
		$url = wc_get_cart_url();
	}

	if ( 'checkout' === $sp_add_to_cart_redirect &&
		function_exists( 'wc_get_checkout_url' ) ) {
		$url = wc_get_checkout_url();
	}

	return $url;
}
if ( in_array( 'woocommerce/woocommerce.php', apply_filters( 'active_plugins', get_option( 'active_plugins' ) ) ) ) {
	add_filter( 'woocommerce_add_to_cart_redirect', 'seedprod_pro_wc_add_to_cart_redirect' );
}




/**
 * Prevent redirect / expired session error if cart is empty while previewing page
 */
function seedprod_pro_wc_prevent_redirect_empty_cart() {
	// First, check whether or not this is a SeedProd page
	$is_seedprod = false;

	$id_from_post = get_the_ID();
	if ( ! empty( get_post_meta( $id_from_post, '_seedprod_page', true ) ) &&
		get_post_meta( $id_from_post, '_seedprod_page', true ) ) {
		$is_seedprod = true;
	}

	// We have to check a second way because of an ajax call that
	// WooCommerce initiates after the page is loaded
	$referer     = wp_get_referer();
	$has_page_id = $referer ? preg_match( '/(page_id=)(\d+)(&|$)/', $referer, $referer_matches ) : '';
	$id_from_uri = $has_page_id ? $referer_matches[2] : '';
	if ( ! empty( $id_from_uri ) &&
		! empty( get_post_meta( $id_from_uri, '_seedprod_page', true ) ) &&
		get_post_meta( $id_from_uri, '_seedprod_page', true ) ) {
		$is_seedprod = true;
	}

	if ( $is_seedprod ) {
		// Next, check that the page is a preview using both methods
		if ( is_preview() ||
			stripos( $referer, 'preview=true' ) !== false ) {

			// Prevent redirect / error message if cart is empty
			add_filter( 'woocommerce_checkout_redirect_empty_cart', '__return_false' );
			add_filter( 'woocommerce_checkout_update_order_review_expired', '__return_false' );
		}
	}
}
if ( in_array( 'woocommerce/woocommerce.php', apply_filters( 'active_plugins', get_option( 'active_plugins' ) ) ) ) {
	add_action( 'template_redirect', 'seedprod_pro_wc_prevent_redirect_empty_cart', 0 );
}




/**
 * Extract Page CSS
 */
function seedprod_pro_extract_page_css( $html, $id = null ) {
	if ( empty( $html ) ) {
		$output         = array();
		$output['css']  = '';
		$output['html'] = '';
		return $output;
	}
	require_once SEEDPROD_PRO_PLUGIN_PATH . 'app/includes/simple_html_dom.php';
	$phtml = seedprod_str_get_html( $html );

	// find all inline styles
	$inline_styles = $phtml->find( '*[style]' );
	$css_inline    = array();
	foreach ( $inline_styles as $k => $v ) {
		// see if the inline style has an id, if not create one.
		if ( empty( $v->id ) ) {
			$v->id = 'sp-' . seedprod_pro_generate_random_string();
		}
		// record id and styles and remove inline styles
		$css_inline[ $v->id ] = seedprod_pro_semi_it( $v->style );
		$v->style             = null;
	}

	// find all style tags
	$style_tags = $phtml->find( 'style' );
	$css_styles = array();
	foreach ( $style_tags as $k2 => $v2 ) {
		// find style tags and record
		$css_styles[] = $v2->innertext;
		// remove style tags
		$v2->outertext = '';
	}

	$output         = array();
	$output['css']  = '';
	$output['html'] = '';

	// Generate css.
	foreach ( $css_styles as $k3 => $v3 ) {
		$output['css'] .= $v3;
	}
	foreach ( $css_inline as $k2 => $v2 ) {
		$output['css'] .= '#' . $k2 . '{' . $v2 . '}';
	}

	// Get extra css.
	global $wpdb;
	$tablename = $wpdb->prefix . 'posts';
	$sql       = "SELECT * FROM $tablename WHERE id = %d";
	$safe_sql  = $wpdb->prepare( $sql, $id ); // phpcs:ignore WordPress.DB.PreparedSQL.NotPrepared
	$lpage     = $wpdb->get_row( $safe_sql ); // phpcs:ignore WordPress.DB.PreparedSQL.NotPrepared

	if ( ! empty( $lpage->post_content_filtered ) ) {
		$settings = json_decode( $lpage->post_content_filtered );
		// Get mobile css.
		$mobile_css = '';
		if ( ! empty( $settings->document->settings->mobileCss ) ) {
			$mobile_css = $settings->document->settings->mobileCss;
			$mobile_css = str_replace( '.sp-mobile-view', '', $mobile_css );
		}

		// Get device mobile css.
		$device_mobile_css = '';
		if ( ! empty( $settings->document->settings->mobileVisibilityCss ) ) {
			$device_mobile_css = $settings->document->settings->mobileVisibilityCss;
			$device_mobile_css = str_replace( '.sp-mobile-view', '', $device_mobile_css );
		}

		// Get desktop visibility css.
		$device_desktop_css = '';
		if ( ! empty( $settings->document->settings->desktopVisibilityCss ) ) {
			$device_desktop_css = $settings->document->settings->desktopVisibilityCss;
		}

		if ( ! empty( $mobile_css ) || ! empty( $device_mobile_css ) ) {
			$mobile_css = '@media only screen and (max-width: 480px) {' . $mobile_css . $device_mobile_css . '}';
		}

		if ( ! empty( $device_desktop_css ) ) {
			$device_desktop_css = '@media only screen and (min-width: 480px) {' . $device_desktop_css . '}';
		}

		$output['css'] = $output['css'] . $mobile_css . $device_desktop_css;
	}

	// Clean HTML
	ob_start();
	echo $phtml; // phpcs:ignore WordPress.Security.EscapeOutput.OutputNotEscaped
	$rhtml = ob_get_clean();

	$output['html'] = $rhtml;

	return $output;
}

/**
 * Strip semi colon from string.
 *
 * @param string $string String to be cleaned.
 * @return string Cleaned up string.
 */
function seedprod_pro_semi_it( $string ) {
	return rtrim( $string, ';' ) . ';';
}

/**
 * Generate random string.
 *
 * @param integer $length Length of random string to be generated.
 * @return string $random_string Randomly generated string.
 */
function seedprod_pro_generate_random_string( $length = 10 ) {
	$characters        = '0123456789abcdefghijklmnopqrstuvwxyz';
	$characters_length = strlen( $characters );
	$random_string     = '';
	for ( $i = 0; $i < $length; $i++ ) {
		$random_string .= $characters[ wp_rand( 0, $characters_length - 1 ) ];
	}
	return $random_string;
}

/**
 * Generate CSS file.
 *
 * @param string $id  CSS page ID.
 * @param string $css CSS classes.
 * @return void
 */
function seedprod_pro_generate_css_file( $id, $css ) {
	// create folder for css is it does not exist
	$uploads_dir = trailingslashit( wp_upload_dir()['basedir'] ) . 'seedprod-css';
	$url         = add_query_arg(
		array(
			'page' => 'seedprod_pro',
		),
		admin_url( 'admin.php' )
	);
	$url         = esc_url( $url );
	if ( wp_mkdir_p( $uploads_dir ) ) {
		$creds = request_filesystem_credentials( $url, '', false, false, null );
		if ( false === $creds ) {
			return;
		}
		if ( ! WP_Filesystem( $creds ) ) {
			request_filesystem_credentials( $url, '', true, false, null );
			return;
		}

		// locate global css id.
		$global_css_page_id = get_option( 'seedprod_global_css_page_id' );
		if ( $global_css_page_id == $id ) {
			$id = 'global';
		}

		// clean empty css statements.
		$regexpcss = '/([a-z\-])+:\s*;\s*/m';
		$css       = preg_replace( $regexpcss, '', $css );

		// clean empty css classes.
		$regexpcss2 = '/[^{}\r\n]+(?:\r?\n[^{}\r\n]+)*{\s*}/';
		$css        = preg_replace( $regexpcss2, '', $css );

		// create file.
		global $wp_filesystem;
		$r = $wp_filesystem->put_contents(
			$uploads_dir . '/style-' . $id . '.css',
			$css,
			FS_CHMOD_FILE // predefined mode settings for WP files
		);
	}
}

