/*********

 ********

 ************ ajax picture upload

 ********

 ***********/

function userpro_ajax_picupload() {

    jQuery(".userpro-pic-upload").each(function () {

        var allowed = jQuery(this).data('allowed_extensions');

        var filetype = jQuery(this).data('filetype');

        var form = jQuery(this).parents('.userpro').find('form');

        var user_id = jQuery( 'input[id^="user_id-"]' ).val();


        jQuery(this).uploadFile({

            url: userpro_ajax_url + '?action=userpro_ajax_fileupload&user_id='+user_id,

            allowedTypes: allowed,

            onSubmit: function (files) {

                jQuery('.userpro-webcam-pic-upload').hide();

                var statusbar = jQuery('.ajax-file-upload-statusbar:visible');

                statusbar.parents('.userpro-input').find('.red').hide();

                if (statusbar.parents('.userpro-input').find('img.default').length) {

                    statusbar.parents('.userpro-input').find('img.default').show();

                    statusbar.parents('.userpro-input').find('img.modified').remove();

                }

            },

            onSuccess: function (files, data, xhr) {

                var statusbar = jQuery('.ajax-file-upload-statusbar:visible');

                data = jQuery.parseJSON(data);

                if (data.status == 2) {

                    alert('File size exceeds allowed file size limit.');

                    statusbar.hide();

                    return;

                }

                if (data.status == 0) {

                    alert('Invalid file type.');

                    statusbar.hide();

                    return;

                }

                var src = data.target_file_uri;

                if (statusbar.parents('.userpro-input').find('img.default').length) {

                    var width = statusbar.parents('.userpro-input').find('img.default').attr('width');

                    var height = statusbar.parents('.userpro-input').find('img.default').attr('height');

                } else if (statusbar.parents('.userpro-input').find('img.modified').length) {

                    var width = statusbar.parents('.userpro-input').find('img.modified').attr('width');

                    var height = statusbar.parents('.userpro-input').find('img.modified').attr('height');

                } else if (statusbar.parents('.userpro-input').find('img.avatar').length) {

                    var width = statusbar.parents('.userpro-input').find('img.avatar').attr('width');

                    var height = statusbar.parents('.userpro-input').find('img.avatar').attr('height');

                }


                str = 'action=userpro_crop_picupload&filetype=' + filetype + '&width=' + width + '&height=' + height + '&src=' + src;

                jQuery.ajax({

                    url: userpro_ajax_url,

                    data: str,

                    dataType: 'JSON',

                    type: 'POST',

                    success: function (data) {

                        statusbar.prev().after("<input type='button' value='" + statusbar.parents('.userpro-input').find('.userpro-pic').data('remove_text') + "' class='userpro-button red' style='display:none' />");

                        statusbar.prev().fadeIn(function () {

                            if (filetype == 'picture') {

                                statusbar.parents('.userpro-input').find('.userpro_post_feature_img').remove();

                                statusbar.parents('.userpro-input').find('img').attr('src', data.response);

                                statusbar.parents('.userpro-input').find('img').removeClass('no_feature');

                                jQuery('.userpro-webcam-pic-upload').show();


                            } else if (filetype == 'file') {


                                statusbar.parents('.userpro-input').find('.userpro-file-input').remove();

                                statusbar.parents('.userpro-input').prepend(data.response);


                            }

                            statusbar.hide();

                        });

                        statusbar.parents('.userpro-input').find('input:hidden').val(src);

                        statusbar.parents('.userpro-input').find('.userpro-pic-none').hide();


                        // re-validate

                        form.find('input').each(function () {

                            jQuery(this).trigger('blur');

                        });


                    }

                });


            }

        });

    });

}


/*********

 ********

 ************ password strength meter

 ********

 ***********/

function userpro_password_strength_meter(element) {

    var meter = element.parents('.userpro').find(".userpro-field[data-key^='passwordstrength']");

    var meter_data = meter.find('span.strength-text').data();

    var meter_text = meter.find('span.strength-text');

    var password = element.val();

    var LOWER = /[a-z]/,

        UPPER = /[A-Z]/,

        DIGIT = /[0-9]/,

        DIGITS = /[0-9].*[0-9]/,

        SPECIAL = /[^a-zA-Z0-9]/,

        SAME = /^(.)\1+$/;

    var lower = LOWER.test(password),

        upper = UPPER.test(password.substring(0, 1).toLowerCase() + password.substring(1)),

        digit = DIGIT.test(password),

        digits = DIGITS.test(password),

        special = SPECIAL.test(password);

    if (meter.length > 0) {

        if (password.length < 8) {

            meter.find('.strength-plain').removeClass('fill');

            meter_text.html(meter_data['too_short']);

            return 0;

        } else if (SAME.test(password)) {

            meter.find('.strength-plain').removeClass('fill');

            meter.find('.strength-plain:eq(0)').addClass('fill');

            meter_text.html(meter_data['very_weak']);

            return 1;

        } else if (lower && upper && digit && special) {

            meter.find('.strength-plain').removeClass('fill');

            meter.find('.strength-plain').addClass('fill');

            meter_text.html(meter_data['very_strong']);

            return 5;

        } else if (lower && upper && digit || lower && digits || upper && digits || special) {

            meter.find('.strength-plain').removeClass('fill');

            meter.find('.strength-plain:eq(0),.strength-plain:eq(1),.strength-plain:eq(2),.strength-plain:eq(3)').addClass('fill');

            meter_text.html(meter_data['strong']);

            return 4;

        } else if (lower && upper || lower && digit || upper && digit) {

            meter.find('.strength-plain').removeClass('fill');

            meter.find('.strength-plain:eq(0),.strength-plain:eq(1),.strength-plain:eq(2)').addClass('fill');

            meter_text.html(meter_data['good']);

            return 3;

        } else {

            meter.find('.strength-plain').removeClass('fill');

            meter.find('.strength-plain:eq(0),.strength-plain:eq(1)').addClass('fill');

            meter_text.html(meter_data['weak']);

            return 2;

        }

    }

}


